mapper.select:
select data_date dataDate ,site_code siteCode ,general_agent_count generalAgentCount ,register_agent_count registerAgentCount ,register_user_count registerUserCount ,login_user_count loginUserCount ,deposit_amount depositAmount ,withdraw_amount withdrawAmount ,turnover_amount turnoverAmount ,prize_amount prizeAmount ,agent_share_amount agentShareAmount ,lottery_rebates_amount lotteryRebatesAmount ,gp1 ,revenue ,gp1_5 gp15 ,gp2 ,update_date updateDate from doris_dt.app_day_site_kpi
and site_code = #{siteCode}
order by ${sort} ${order}
order by data_date desc, site_code desc
limit #{offset}, #{limit}
